const path = require('path');
const fs = require('fs');

var jslib = process.env.JSLIB;
if (jslib == null) {
  throw new Error(`Missing JSLIB environment variable`);
}
var skipPath = path.join(jslib, 'skip.js');
if (!fs.existsSync(skipPath)) {
  throw new Error(`Invalid JSLIB environment variable: '${jslib}'`);
}

module.exports = require(skipPath);
